import React from 'react';
import { Link } from 'react-router-dom';

const QuestionDisplay = ({ generatedQuestions }) => {
  // Parse the JSON string into an array
  const questionsObject = JSON.parse(generatedQuestions.questions.join(''));
  const questionsArray = questionsObject.question;

  const handleSaveQuestions = () => {
    const content = questionsArray.join('\n');
    const blob = new Blob([content], { type: 'text/plain' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = 'generated_questions.txt';
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
  };

  return (
    <div>
      <h2>Generated Questions:</h2>
      <div className="question-box">
        <ol>
          {questionsArray.map((question, index) => (
            <li key={index} className="question-item">
              {question}
            </li>
          ))}
        </ol>
      </div>
      <button className="save-button" onClick={handleSaveQuestions}>Save Questions</button>
      <Link to="/">Back to Home</Link>
    </div>
  );
};

export default QuestionDisplay;
